import React, { useState } from 'react';

interface Question {
  questionText: string;
  options: string[];
}

const SurveyCreator: React.FC = () => {
  const [surveyTitle, setSurveyTitle] = useState('');
  const [questions, setQuestions] = useState<Question[]>([{ questionText: '', options: [''] }]);

  const handleTitleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setSurveyTitle(e.target.value);
  };

  const handleQuestionChange = (index: number, value: string) => {
    const newQuestions = [...questions];
    newQuestions[index].questionText = value;
    setQuestions(newQuestions);
  };

  const handleOptionChange = (qIndex: number, oIndex: number, value: string) => {
    const newQuestions = [...questions];
    newQuestions[qIndex].options[oIndex] = value;
    setQuestions(newQuestions);
  };

  const addQuestion = () => {
    setQuestions([...questions, { questionText: '', options: [''] }]);
  };

  const addOption = (qIndex: number) => {
    const newQuestions = [...questions];
    newQuestions[qIndex].options.push('');
    setQuestions(newQuestions);
  };

  const handleSubmit = () => {
    console.log({ surveyTitle, questions });
    // Vous pouvez envoyer les données à votre backend ici
  };

  return (
    <div>
      <h2>Créer un Sondage</h2>
      <input type="text" value={surveyTitle} onChange={handleTitleChange} placeholder="Titre du sondage" />
      {questions.map((question, qIndex) => (
        <div key={qIndex}>
          <input
            type="text"
            value={question.questionText}
            onChange={(e) => handleQuestionChange(qIndex, e.target.value)}
            placeholder={`Question ${qIndex + 1}`}
          />
          {question.options.map((option, oIndex) => (
            <input
              key={oIndex}
              type="text"
              value={option}
              onChange={(e) => handleOptionChange(qIndex, oIndex, e.target.value)}
              placeholder={`Option ${oIndex + 1}`}
            />
          ))}
          <button onClick={() => addOption(qIndex)}>Ajouter une option</button>
        </div>
      ))}
      <button onClick={addQuestion}>Ajouter une question</button>
      <button onClick={handleSubmit}>Soumettre le sondage</button>
    </div>
  );
};

export default SurveyCreator;
