import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from '../auth/AuthContext';
import DocumentUploader from './DocumentUploader';

interface Document {
  id: number;
  description: string;
  text: string;
  content: string;
  fileUrl: string;
  createdAt: Date;
  updatedAt: Date;
}

const Vault: React.FC = () => {
  const auth = useAuth();
  const [documents, setDocuments] = useState<Document[]>([]);
  const [showUploader, setShowUploader] = useState(false);

  useEffect(() => {
    const fetchDocuments = async () => {
      try {
        const response = await axios.get('http://localhost:3000/documents', { withCredentials: true });
        setDocuments(response.data.document); 
        console.log(response.data.doc)
      } catch (error) {
        console.error('Error fetching documents:', error);
      }
    };

    fetchDocuments();
  }, []);

  const handleToggleUploader = () => {
    setShowUploader(!showUploader);
  };

  if (!auth.user) {
    return <p>Veuillez vous connecter.</p>;
  }

  return (
    <div>
      <h2>Coffre-fort numérique</h2>
      {auth.user.role === 'admin' && (
        <div>
          <p>Accès complet pour l'Admin.</p>
          <button onClick={handleToggleUploader}>
            {showUploader ? 'Masquer' : 'Ajouter un fichier'}
          </button>
          {showUploader && <DocumentUploader />}
        </div>
      )}
      {auth.user.role === 'user' && <p>Accès limité pour l'Utilisateur.</p>}
      {auth.user.role === 'readonly' && <p>Accès en lecture seule.</p>}

      <div>
        <h3>Documents</h3>
        {documents.length > 0 ? (
          <ul>
            {documents.map((doc) => (
              <li key={doc.id}>
                <h4>{doc.description}</h4>
                <p>{doc.text}</p>
                <a href={doc.fileUrl} target="_blank" rel="noopener noreferrer">
                  Télécharger le fichier
                </a>
                <p>Créé le: {new Date(doc.createdAt).toLocaleDateString()}</p>
                <p>Mis à jour le: {new Date(doc.updatedAt).toLocaleDateString()}</p>
              </li>
            ))}
          </ul>
        ) : (
          <p>Aucun document disponible.</p>
        )}
      </div>
    </div>
  );
};

export default Vault;
