import React, { useState } from 'react';
import SurveyCreator from './components/SurveyCreator';
import SurveyList from './components/SurveyList';
import SurveyResponse from './components/SurveyResponse';
import SurveyResults from './components/SurveyResults';

interface Question {
  questionText: string;
  options: string[];
}

interface Survey {
  title: string;
  questions: Question[];
}

const App: React.FC = () => {
  const [urveys, setSurveys] = useState<Survey[]>([]);
  const [responses, setResponses] = useState<string[][][]>([]);
  const [showSurveys, setShowSurveys] = useState(false);
  const [showResults, setShowResults] = useState(false);

  const addSurvey = (urvey: Survey) => {
    setSurveys([...urveys, urvey]);
  };

  const handleResponseSubmit = (urveyIndex: number, response: string[]) => {
    const newResponses = [...responses];
    if (!newResponses[urveyIndex]) {
      newResponses[urveyIndex] = [];
    }
    newResponses[urveyIndex].push(response);
    setResponses(newResponses);
  };

  return (
    <div>
      <SurveyCreator onAddSurvey={addSurvey} />
      <button onClick={() => setShowSurveys(!showSurveys)}>
        {showSurveys ? "Masquer les sondages" : "Afficher les sondages"}
      </button>
      {showSurveys && <SurveyList urveys={urveys} />}

      <button onClick={() => setShowResults(!showResults)}>
        {showResults ? "Masquer les résultats" : "Afficher les résultats des sondages"}
      </button>
      {showResults && urveys.map((urvey, index) => (
        <SurveyResults key={index} urvey={urvey} responses={responses[index] || []} />
      ))}

      {urveys.map((urvey, index) => (
        <SurveyResponse
          key={index}
          urvey={urvey}
          onSubmit={(response) => handleResponseSubmit(index, response)}
        />
      ))}
    </div>
  );
};

export default App;
