import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from './auth/AuthContext';

interface User {
  id: number;
  username: string;
  role: string;
}

const UserManager: React.FC = () => {
  const auth = useAuth();
  const [users, setUsers] = useState<User[]>([]);
  console.log(users)

  const fetchUsers = async () => {
    try {
      const token = "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VySWQiOjIsInVzZXJuYW1lIjoiY2xlbWkiLCJyb2xlcyI6MSwiaWF0IjoxNzE3ODYzMDMyLCJleHAiOjE3MTc5NDk0MzJ9.5_wxfrpIJq2vAlkSi8SU7uKXnkbBJt1F28qfrtzYlRc";  

      const response = await axios.get('http://localhost:3000/users', {
        headers: {
          'Authorization': `Bearer ${token}`
        },
        withCredentials: true
      });
      setUsers(response.data.user);
    } catch (error) {
      alert('Error fetching users');
    }
  };

  const updateUserRole = async (userId: number, newRoleId: string) => {
      if(newRoleId == "null")
        return
    try {
      const token = "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VySWQiOjIsInVzZXJuYW1lIjoiY2xlbWkiLCJyb2xlcyI6MSwiaWF0IjoxNzE3ODYzMDMyLCJleHAiOjE3MTc5NDk0MzJ9.5_wxfrpIJq2vAlkSi8SU7uKXnkbBJt1F28qfrtzYlRc";

      await axios.put(`http://localhost:3000/users/${userId}/role`, { roleId: parseInt(newRoleId) }, {
        headers: {
          'Authorization': `Bearer ${token}`
        },
        withCredentials: true
      });
      fetchUsers();
      alert("Nouveau rôle attribué") 
    } catch (error) {
      alert('Error updating user role');
    }
  };

  const deleteUser = async (userId: number) => {
    try {
      const token = "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VySWQiOjIsInVzZXJuYW1lIjoiY2xlbWkiLCJyb2xlcyI6MSwiaWF0IjoxNzE3ODYzMDMyLCJleHAiOjE3MTc5NDk0MzJ9.5_wxfrpIJq2vAlkSi8SU7uKXnkbBJt1F28qfrtzYlRc";

      await axios.delete(`http://localhost:3000/users/${userId}`, {
        headers: {
          'Authorization': `Bearer ${token}`
        },
        withCredentials: true
      });
      fetchUsers(); // Refresh the user list
    } catch (error) {
      alert('Error deleting user');
    }
  };

  useEffect(() => {
    fetchUsers();
  }, []);

  if (!auth.user) {
    return <p>Veuillez vous connecter.</p>;
  }

  return (
    <div>
      <h2>Gestion des utilisateurs</h2>
      {users.length > 0 ? (
        <ul>
          {users.map((user) => (
            <li key={user.id}>
              <p>Username: {user.username}</p>
              <p>Role: {user.role}</p>
              <select value={user.role} onChange={(e) => updateUserRole(user.id, e.target.value)}>
                <option value="null">-</option>
                <option value="1">Admin</option>
                <option value="2">User</option>
                <option value="3">Read-only</option>
              </select>
              <br/>
              <button onClick={() => deleteUser(user.id)}>Supprimer</button>
            </li>
          ))}
        </ul>
      ) : (
        <p>Aucun utilisateur disponible.</p>
      )}
    </div>
  );
};

export default UserManager;
