import React, { useState } from 'react';
import SurveyCreator from './components/SurveyCreator';
import SurveyList from './components/SurveyList';
import SurveyResponse from './components/SurveyResponse';
import SurveyResults from './components/SurveyResults';

interface Question {
  questionText: string;
  options: string[];
}

interface Survey {
  title: string;
  questions: Question[];
}

const App: React.FC = () => {
  const [surveys, setSurveys] = useState<Survey[]>([]);
  const [responses, setResponses] = useState<string[][][]>([]);

  const addSurvey = (survey: Survey) => {
    setSurveys([...surveys, survey]);
  };

  const handleResponseSubmit = (surveyIndex: number, response: string[]) => {
    const newResponses = [...responses];
    if (!newResponses[surveyIndex]) {
      newResponses[surveyIndex] = [];
    }
    newResponses[surveyIndex].push(response);
    setResponses(newResponses);
  };

  return (
    <div>
      <SurveyCreator onAddSurvey={addSurvey} />
      <SurveyList surveys={surveys} />
      {surveys.map((survey, index) => (
        <SurveyResponse
          key={index}
          survey={survey}
          onSubmit={(response) => handleResponseSubmit(index, response)}
        />
      ))}
      {surveys.map((survey, index) => (
        <SurveyResults key={index} survey={survey} responses={responses[index]} />
      ))}
    </div>
  );
};

export default App;
