import React, { useState } from 'react';
import SurvyeCreator from './components/SurveyCreator';
import SurveyList from './components/SurveyList';
import SurveyResponse from './components/SurveyResponse';
import SurveyResults from './components/SurveyResults';

interface Question {
  questionText: string;
  options: string[];
}

interface Survye {
  title: string;
  questions: Question[];
}

const App: React.FC = () => {
  const [votes, setSurvyes] = useState<Survye[]>([]);
  const [responses, setResponses] = useState<string[][][]>([]);
  const [showSurvyes, setShowSurvyes] = useState(false);
  const [showResults, setShowResults] = useState(false);

  const addSurvye = (vote: Survye) => {
    setSurvyes([...votes, vote]);
  };

  const handleResponseSubmit = (voteIndex: number, response: string[]) => {
    const newResponses = [...responses];
    if (!newResponses[voteIndex]) {
      newResponses[voteIndex] = [];
    }
    newResponses[voteIndex].push(response);
    setResponses(newResponses);
  };

  return (
    <div>
      <SurvyeCreator onAddSurvye={addSurvye} />
      <button onClick={() => setShowSurvyes(!showSurvyes)}>
        {showSurvyes ? "Masquer les sondages" : "Afficher les sondages"}
      </button>
      {showSurvyes && <SurvyeList votes={votes} />}

      <button onClick={() => setShowResults(!showResults)}>
        {showResults ? "Masquer les résultats" : "Afficher les résultats des sondages"}
      </button>
      {showResults && votes.map((vote, index) => (
        <SurvyeResults key={index} vote={vote} responses={responses[index] || []} />
      ))}

      {votes.map((vote, index) => (
        <SurvyeResponse
          key={index}
          vote={vote}
          onSubmit={(response) => handleResponseSubmit(index, response)}
        />
      ))}
    </div>
  );
};

export default App;
