import React, { useState } from 'react';
import VoteCreator from './components/VoteCreator';
import VoteList from './components/VoteList';
import VoteResponse from './components/VoteResponse';
import VoteResults from './components/VoteResults';

interface Question {
  questionText: string;
  options: string[];
}

interface Vote {
  title: string;
  questions: Question[];
}

const App: React.FC = () => {
  const [surveys, setVotes] = useState<Vote[]>([]);
  const [responses, setResponses] = useState<string[][][]>([]);
  const [showVotes, setShowVotes] = useState(false);
  const [showResults, setShowResults] = useState(false);

  const addVote = (survey: Vote) => {
    setVotes([...surveys, survey]);
  };

  const handleResponseSubmit = (surveyIndex: number, response: string[]) => {
    const newResponses = [...responses];
    if (!newResponses[surveyIndex]) {
      newResponses[surveyIndex] = [];
    }
    newResponses[surveyIndex].push(response);
    setResponses(newResponses);
  };

  return (
    <div>
      <VoteCreator onAddVote={addVote} />
      <button onClick={() => setShowVotes(!showVotes)}>
        {showVotes ? "Masquer les sondages" : "Afficher les sondages"}
      </button>
      {showVotes && <VoteList surveys={surveys} />}

      <button onClick={() => setShowResults(!showResults)}>
        {showResults ? "Masquer les résultats" : "Afficher les résultats des sondages"}
      </button>
      {showResults && surveys.map((survey, index) => (
        <VoteResults key={index} survey={survey} responses={responses[index] || []} />
      ))}

      {surveys.map((survey, index) => (
        <VoteResponse
          key={index}
          survey={survey}
          onSubmit={(response) => handleResponseSubmit(index, response)}
        />
      ))}
    </div>
  );
};

export default App;
