import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from './auth/AuthContext';

interface User {
  id: number;
  username: string;
  role: string;
}

const UserManager: React.FC = () => {
  const auth = useAuth();
  const [users, setUsers] = useState<User[]>([]);

  const fetchUsers = async () => {
    try {
      const token = "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VySWQiOjIsInVzZXJuYW1lIjoiY2xlbWkiLCJyb2xlcyI6MSwiaWF0IjoxNzE3ODYzMDMyLCJleHAiOjE3MTc5NDk0MzJ9.5_wxfrpIJq2vAlkSi8SU7uKXnkbBJt1F28qfrtzYlRc"; // Assurez-vous que le token est disponible dans le contexte d'authentification

      const response = await axios.get('http://localhost:3000/users', {
        headers: {
          'Authorization': `Bearer ${token}`
        },
        withCredentials: true
      });
      setUsers(response.data);
    } catch (error) {
      alert('Error fetching users');
    }
  };

  useEffect(() => {
    fetchUsers();
  }, []);

  if (!auth.user) {
    return <p>Veuillez vous connecter.</p>;
  }

  return (
    <div>
      <h2>Gestion des utilisateurs</h2>
      {users.length > 0 ? (
        <ul>
          {users.map((user) => (
            <li key={user.id}>
              <p>Username: {user.username}</p>
              <p>Role: {user.role}</p>
              {/* Add role update and delete buttons */}
            </li>
          ))}
        </ul>
      ) : (
        <p>Aucun utilisateur disponible.</p>
      )}
    </div>
  );
};

export default UserManager;
