import React, { useState } from 'react';
import SurveyCreator from './components/VoteCreator';
import SurveyList from './components/SurveyList';
import SurveyResponse from './components/SurveyResponse';
import SurveyResults from './components/VoteResults';

interface Question {
  questionText: string;
  options: string[];
}

interface Survey {
  title: string;
  questions: Question[];
}

const App: React.FC = () => {
  const [surveys, setSurveys] = useState<Survey[]>([]);
  const [responses, setResponses] = useState<string[][][]>([]);
  const [showSurveys, setShowSurveys] = useState(false);
  const [showResults, setShowResults] = useState(false);

  const addSurvey = (survey: Survey) => {
    setSurveys([...surveys, survey]);
  };

  const handleResponseSubmit = (surveyIndex: number, response: string[]) => {
    const newResponses = [...responses];
    if (!newResponses[surveyIndex]) {
      newResponses[surveyIndex] = [];
    }
    newResponses[surveyIndex].push(response);
    setResponses(newResponses);
  };

  return (
    <div>
      <SurveyCreator onAddSurvey={addSurvey} />
      <button onClick={() => setShowSurveys(!showSurveys)}>
        {showSurveys ? "Masquer les sondages" : "Afficher les sondages"}
      </button>
      {showSurveys && <SurveyList surveys={surveys} />}

      <button onClick={() => setShowResults(!showResults)}>
        {showResults ? "Masquer les résultats" : "Afficher les résultats des sondages"}
      </button>
      {showResults && surveys.map((survey, index) => (
        <SurveyResults key={index} survey={survey} responses={responses[index] || []} />
      ))}

      {surveys.map((survey, index) => (
        <SurveyResponse
          key={index}
          survey={survey}
          onSubmit={(response) => handleResponseSubmit(index, response)}
        />
      ))}
    </div>
  );
};

export default App;
