import React, { useState } from 'react';

interface SurveyQuestion {
  questionText: string;
  options?: string[];
  type: 'radio' | 'text';
}

interface Survey {
  id: number;
  title: string;
  questions: SurveyQuestion[];
}

interface SurveyResponseProps {
  survey: Survey;
  onSubmit: (response: any) => void;
}

const SurveyResponse: React.FC<SurveyResponseProps> = ({ survey, onSubmit }) => {
  const [responses, setResponses] = useState<string[]>([]);

  const handleResponseChange = (index: number, value: string) => {
    const newResponses = [...responses];
    newResponses[index] = value;
    setResponses(newResponses);
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    onSubmit(responses);
  };

  return (
    <form onSubmit={handleSubmit}>
      <h3>RÃ©pondre au sondage: {survey.title}</h3>
      {survey.questions.map((question, qIndex) => (
        <div key={qIndex}>
          <h4>{question.questionText}</h4>
          {question.type === 'radio' && question.options?.map((option, oIndex) => (
            <label key={oIndex}>
              <input
                type="radio"
                name={`question-${qIndex}`}
                value={option}
                onChange={(e) => handleResponseChange(qIndex, e.target.value)}
              />
              {option}
            </label>
          ))}
          {question.type === 'text' && (
            <input
              type="text"
              onChange={(e) => handleResponseChange(qIndex, e.target.value)}
            />
          )}
        </div>
      ))}
      <button type="submit">Soumettre</button>
    </form>
  );
};

export default SurveyResponse;
