import React, { useState } from 'react';
import SurveyCreator from './components/SurveyCreator'; 
import SurveyResponse from './components/SurveyResponse';
import SurveyResults from './components/SurveyResults';
import VoteCreator from './components/VoteCreator'; 
import VoteResponse from './components/VoteResponse';
import VoteResults from './components/VoteResults';

interface SurveyQuestion {
  questionText: string;
  options: string[];
}

interface Survey {
  title: string;
  questions: SurveyQuestion[];
}

interface VoteQuestion {
  questionText: string;
  options: string[];
}

interface Vote {
  title: string;
  questions: VoteQuestion[];
  mode: string;
}

const App: React.FC = () => {
  const [surveys, setSurveys] = useState<Survey[]>([]);
  const [surveyResponses, setSurveyResponses] = useState<string[][][]>([]);
  const [votes, setVotes] = useState<Vote[]>([]);
  const [voteResponses, setVoteResponses] = useState<string[][][]>([]);
  const [showSurveys, setShowSurveys] = useState(false);
  const [showSurveyResults, setShowSurveyResults] = useState(false);
  const [showVotes, setShowVotes] = useState(false);
  const [showVoteResults, setShowVoteResults] = useState(false);

  const addSurvey = (survey: Survey) => {
    setSurveys([...surveys, survey]);
  };

  const handleSurveyResponseSubmit = (surveyIndex: number, response: string[]) => {
    const newResponses = [...surveyResponses];
    if (!newResponses[surveyIndex]) {
      newResponses[surveyIndex] = [];
    }
    newResponses[surveyIndex].push(response);
    setSurveyResponses(newResponses);
  };

  const addVote = (vote: Vote) => {
    setVotes([...votes, vote]);
  };

  const handleVoteResponseSubmit = (voteIndex: number, response: string[]) => {
    const newResponses = [...voteResponses];
    if (!newResponses[voteIndex]) {
      newResponses[voteIndex] = [];
    }
    newResponses[voteIndex].push(response);
    setVoteResponses(newResponses);
  };

  const toggleSurveys = () => {
    setShowSurveys(!showSurveys);
    setShowVotes(false);
  };

  const toggleVotes = () => {
    setShowVotes(!showVotes);
    setShowSurveys(false);
  };

  return (
    <div>
      <h1>Gestion des Sondages et des Votes</h1>

      <button onClick={toggleSurveys}>
        {showSurveys ? "Masquer les sondages" : "Afficher les sondages"}
      </button>
      <button onClick={toggleVotes}>
        {showVotes ? "Masquer les votes" : "Afficher les votes"}
      </button>

      {showSurveys && (
        <>
          <SurveyCreator onAddSurvey={addSurvey} />
          <button onClick={() => setShowSurveyResults(!showSurveyResults)}>
            {showSurveyResults ? "Masquer les résultats des sondages" : "Afficher les résultats des sondages"}
          </button> 

          {surveys.map((survey, index) => (
            <SurveyResponse
              key={index}
              survey={survey}
              onSubmit={(response) => handleSurveyResponseSubmit(index, response)}
            />
          ))}
          <br/>
          {showSurveyResults && surveys.map((survey, index) => (
            <SurveyResults key={index} survey={survey} responses={surveyResponses[index] || []} />
          ))}

        </>
      )}

      {showVotes && (
        <>
          <VoteCreator onAddVote={addVote} />
          <button onClick={() => setShowVoteResults(!showVoteResults)}>
            {showVoteResults ? "Masquer les résultats des votes" : "Afficher les résultats des votes"}
          </button> 
          {showVoteResults && votes.map((vote, index) => (
            <VoteResults key={index} vote={vote} responses={voteResponses[index] || []} />
          ))}
          {votes.map((vote, index) => (
            <VoteResponse
              key={index}
              vote={vote}
              onSubmit={(response) => handleVoteResponseSubmit(index, response)}
            />
          ))}
        </>
      )}
    </div>
  );
};

export default App;
