import React from 'react';
import { useAuth } from '../auth/AuthContext';

const Vault: React.FC = () => {
  const auth = useAuth();

  if (!auth.user) {
    return <p>Veuillez vous connecter.</p>;
  }

  return (
    <div>
      <h2>Coffre-fort numérique</h2>
      {auth.user.role === 'admin' && <p>Accès complet pour l'Admin.</p>}
      {auth.user.role === 'user' && <p>Accès limité pour l'Utilisateur.</p>}
      {auth.user.role === 'readonly' && <p>Accès en lecture seule.</p>}
    </div>
  );
};

export default Vault;
